import React, { useState, useEffect, useRef } from 'react';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const sidebarRef = useRef(null);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleOuterClick = (event) => {
    if (sidebarRef.current && !sidebarRef.current.contains(event.target)) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    if (isOpen) {
      document.addEventListener('click', handleOuterClick);
    } else {
      document.removeEventListener('click', handleOuterClick);
    }
    return () => {
      document.removeEventListener('click', handleOuterClick);
    };
  }, [isOpen]);

  return (
    <div>
      <button
        onClick={toggleSidebar}
        className="p-2 bg-blue-500 text-white rounded-md"
      >
        Toggle Sidebar
      </button>

      <div
        ref={sidebarRef}
        className={`fixed top-0 left-0 h-full bg-gray-800 text-white shadow-md transition-transform ${
          isOpen ? 'translate-x-0' : '-translate-x-full'
        }`}
        style={{ width: '250px' }}
      >
        <button
          onClick={toggleSidebar}
          className="p-2 text-gray-400 hover:text-white absolute top-2 right-2"
        >
          Close
        </button>
        <ul className="mt-10 space-y-4 px-4">
          <li>
            <a href="#home" className="block py-2 hover:bg-gray-700">
              Home
            </a>
          </li>
          <li>
            <a href="#about" className="block py-2 hover:bg-gray-700">
              About
            </a>
          </li>
          <li>
            <a href="#services" className="block py-2 hover:bg-gray-700">
              Services
            </a>
          </li>
          <li>
            <a href="#contact" className="block py-2 hover:bg-gray-700">
              Contact
            </a>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
